import React, { useState } from "react";
import questions from "./questions";

function Quiz() {
  //   const task = questions[1].qn;

  const [task, setTask] = useState("hello");
  let [count, setCount] = useState(0);
  const [mark, setMark] = useState(0);
  const [answer, setAnswer] = useState("");
  const ct = questions[count];
  const cStyle = {
    display: count > 0 && "none",
  };
  function changed(e) {
    const inputText = e.target.value;
    setAnswer(inputText);
  }
  function submitBtn(e) {
    e.preventDefault();
    answer === ct.ans ? setMark(mark + 5) : null;
  }
  function nextBtn(e) {
    e.preventDefault();
    setCount(count + 1);
    console.log(count);
    setTask(ct.qn);
    console.log(ct.qn);
  }
  function startBtn(e) {
    e.preventDefault();
    // console.log(questions[0]);

    setTask(ct.qn);
    // console.log(ct.qn);
  }
  return (
    <div>
      <button style={cStyle} onClick={startBtn}>
        Start Task
      </button>
      <p>{task}</p>
      <br />
      <input
        type="text"
        onChange={changed}
        placeholder="enter answer"
        value={answer}
      />
      <button onClick={submitBtn}>submit</button>
      <br />
      <button onClick={nextBtn}>next</button>
      <p>Marks: {mark}</p>
    </div>
  );
}

export default Quiz;
