  let [count, setCount] = useState(0);
  const [mark, setmark] = useState(0);
  const ct = questions[count];
  const cStyle = {
    display: count > 0 && "none",
  };
  //   function submitBtn(e) {
  //     e.preventDefault();
  //     count = count + 1;
  //   }
  function nextBtn(e) {
    e.preventDefault();
    setCount(count + 1);
    console.log(count);
    setTask(ct.qn);
    console.log(ct.qn);
  }
  function startBtn(e) {
    e.preventDefault();
    // console.log(questions[0]);

    setTask(ct.qn);
    // console.log(ct.qn);
  }
  return (
    <div>
      <button style={cStyle} onClick={startBtn}>
        Start Task
      </button>
      <p>{task}</p>
      {/* <br />
      <input type="text" name="" placeholder="enter answer" value={answer} />
      <button onclick={submitBtn}>submit</button>*/}
      <br />
      <button onClick={nextBtn}>next</button>
      <p>Marks: {mark}</p>
    </div>
  );
}

export default Quiz;
