import React, { useState } from "react";
import questions from "./questions";

function Quiz() {
  //   const task = questions[1].qn;
  const [task, setTask] = useState("Hello! Test your knowledge");
  let [count, setCount] = useState(0);
  const [mark, setMark] = useState(0);
  const [answer, setAnswer] = useState("");
  const currentQuestion = questions[count];
  const [rightAns, setRightAns] = useState("");
  // Removing the start task button
  const customStyle = {
    display: count > 0 && "none",
  };
  // getting the input text
  function changed(e) {
    const inputText = e.target.value;
    setAnswer(inputText);
    console.log(answer);
  }

  function nextBtn(e) {
    e.preventDefault();
    setAnswer("");
    setCount(count + 1);
    setTask(currentQuestion.qn);
    setMark(mark + 5);
    // count >= 8 && setTask("Congrats! you have reached the end");
  }
  // taking away the input block & next button after the questions are done
  const endStyle = { display: count > questions.length && "none" };

  function startBtn(e) {
    e.preventDefault();
    setTask(currentQuestion.qn);
  }
  // function submitBtn(e) {
  //   e.preventDefault();
  //   console.log(currentQuestion.ans);
  //   if (currentQuestion === answer) {
  //     setMark(mark + 5);
  //     console.log(currentQuestion.ans);
  //     console.log(answer);
  //   } else {
  //     setMark(mark + 0);
  //   }

  //   // answer === currentQuestion.ans ? setMark(mark + 5) : null;
  //   console.log(mark);
  // }
  return (
    <div>
      <button style={customStyle} onClick={startBtn}>
        Start Task
      </button>
      <p>{task}</p>
      <br />
      <input
        type="text"
        onChange={changed}
        placeholder="enter answer"
        value={answer}
        style={endStyle}
      />
      <br />
      {/* <button onClick={submitBtn}>submit</button> */}
      <button onClick={nextBtn} style={endStyle}>
        next
      </button>
      <p>Marks: {mark}</p>
      <p>{rightAns}</p>
    </div>
  );
}

export default Quiz;
