import React, { useState } from "react";
import Question from "./Question";
import questions from "./questions";

function Quiz() {
  //   const task = questions[1].qn;
  const [task, setTask] = useState("");
  let [count, setCount] = useState(0);
  let mark = 0;
  const answer = "muka";
  function submitBtn(e) {
    e.preventDefault();
    count = count + 1;
  }
  function nextBtn(e) {
    e.preventDefault();
    mark = mark + 5;
    console.log(mark);
  }
  function startBtn(e) {
    e.preventDefault();
    setCount(0);
    questions.filter((fQn) => {
      return fQn.id === count;
    });
  }
  return (
    <div>
      <button onclick={startBtn}>Start Task</button>
      <p>{task}</p>
      <br />
      <input type="text" name="" placeholder="enter answer" value={answer} />
      <button onclick={submitBtn}>submit</button>
      <br />
      <button onclick={nextBtn}>next</button>
    </div>
  );
}

export default Quiz;
